function p(){ try{ return $items('BUILD_PARAMS',0,0).json.params; }catch{ return {}; } }
const params=p(); const hours=Number(params.timeWindowHours||48); const now=Date.now();
const all=$input.all(); const arr=[]; for(const it of all){ const a=Array.isArray(it.json.articles)? it.json.articles: []; for(const x of a){ if(x && x.url) arr.push(x); } }
const seen=new Set(); const out=[]; for(const a of arr){ const k=a.url; if(!k||seen.has(k)) continue; seen.add(k); out.push(a); }
const filtered=out.filter(a=>{ const t=new Date(a.published_at).getTime(); return isFinite(t) && (now-t)<=hours*3600*1000; });
filtered.sort((a,b)=> new Date(b.published_at)-new Date(a.published_at));
return [{ json:{ category:'startups', articles: filtered } }];

function item(n){ try{ const it=$items(n,0,0); return it && it.json ? it.json : null; }catch(_){ return null; } }
const params = (item('BUILD_PARAMS')?.params) || {};
const owner = params.repoOwner || 'vulcanoai';
const repo  = params.repoName  || 'vulcanoai.github.io';
const branch= params.branch     || 'main';
const base  = `https://api.github.com/repos/${owner}/${repo}/contents`;
const latestPath = params.pathLatest || 'data/startups/feed-latest.json';
const snapshotPath = params.pathSnapshot || ('data/startups/feed-'+new Date().toISOString().slice(0,10)+'.json');
const input = item('AGGREGATE') || $json || {};
const articles = Array.isArray(input.articles) ? input.articles : [];
const latestPayload = JSON.stringify({ version:'v1.0', category:'startups', generated_at: new Date().toISOString(), articles }, null, 2);
const latestContent = Buffer.from(latestPayload).toString('base64');
return [ { json: { target:'latest', base, branch, path: latestPath, content: latestContent, message: 'chore(startups): update feed-latest.json' } }, { json: { target:'snapshot', base, branch, path: snapshotPath, content: latestContent, message: 'chore(startups): snapshot for day' } } ];
